mapper.select:
SELECT DATE_FORMAT(record_time,'%Y-%m') AS date, meter_numb AS meterNumb, IFNULL(MAX(kwh),0) AS max, IFNULL(MIN(kwh),0) AS min from record
1 = 1
AND DATE_FORMAT(record_time,'%Y-%m') IN
#{item}
AND location_factory_numb = #{locationFactoryNumb}
AND meter_numb IN
#{item}
GROUP BY DATE_FORMAT(record_time,'%Y-%m'), meter_numb ORDER BY record_time;

SELECT t.meterNumb as meterNumb, t2.meter_desc as meterDesc, CASE t.date when #{query_date_current_month} THEN IFNULL(t.max,0) - IFNULL(t.min,0) ELSE 0 END 'currentKwh', CASE t.date when #{query_date_pre_month} THEN (IFNULL(t.max,0) - IFNULL(t.min,0)) ELSE 0 END 'preMonthKwh', CASE t.date when #{query_date_pre2_month} THEN (IFNULL(t.max,0) - IFNULL(t.min,0)) ELSE 0 END 'pre2MonthKwh' from ( SELECT DATE_FORMAT(record_time,'%Y-%m') AS date, meter_numb AS meterNumb, IFNULL(MAX(kwh),0) AS max, IFNULL(MIN(kwh),0) AS min from record where DATE_FORMAT(record_time,'%Y-%m') IN (#{query_date_current_month},#{query_date_pre_month},#{query_date_pre2_month})
AND location_factory_numb = #{locationFactoryNumb}
AND meter_numb IN
#{item}
GROUP BY DATE_FORMAT(record_time,'%Y-%m'), meter_numb ORDER BY record_time )t LEFT JOIN meter t2 ON t.meterNumb = t2.meter_numb GROUP BY t.meterNumb , t2.meter_desc

SELECT t.meter_numb as meterNumb, t2.meter_desc as meterDesc, t.record_time as recordTime, t.v_ab AS vAb, t.v_bc AS vBc, t.v_ca AS vCa, t.a_a AS aA, t.a_b AS aB, t.a_c AS aC, t.temp_a AS tempA, t.temp_b AS tempB, t.temp_c AS tempC from record t LEFT JOIN meter t2 ON t.meter_numb = t2.meter_numb
DATE_FORMAT(t.record_time,'%Y-%m-%d %H') >= #{beginDate} AND DATE_FORMAT(t.record_time,'%Y-%m-%d %H') <= #{endDate}
AND t.location_factory_numb = #{locationFactoryNumb}
AND t.meter_numb IN
#{item}

SELECT record_time, IFNULL(v_ab,0) AS v_ab, IFNULL(v_bc,0) AS v_bc, IFNULL(v_ca,0) AS v_ca, IFNULL(a_a,0) AS a_a, IFNULL(a_b,0) AS a_b, IFNULL(a_c,0) AS a_c, IFNULL(temp_a,0) AS temp_a, IFNULL(temp_b,0) AS temp_b, IFNULL(temp_c,0) AS temp_c from record
record_time >= #{beginDate} AND record_time <= #{endDate}
AND location_factory_numb = #{locationFactoryNumb}
AND meter_numb = #{meterNumb}
order by record_time asc
